mapper.select:
SELECT B.FID, B.BULD_NM, B.BULD_NM_DC, B.BULD_MNNM, B.BULD_SLNO, M.RN, M.REP_CN,M.RBP_CN FROM TL_SPRD_MANAGE M, (SELECT DISTINCT * FROM TL_SPBD_BULD
AND RN_CD = #{RN_CD}
AND BULD_MNNM LIKE '%' || #{BONBUN} || '%'
AND BULD_SLNO LIKE '%' || #{BOOBUN} || '%'
) B WHERE B.RN_CD = M.RN_CD ORDER BY BULD_NM
