mapper.select:
SELECT *
FROM 


		TN_USER_STYLE


		TN_USER_STYLE_DEFAULT


WHERE USER_ID = #{USER_ID}

	AND SUBJECT_ID = #{SUBJECT_ID}

SELECT *
FROM 


		TN_USER_STYLE


		TN_USER_STYLE_DEFAULT


WHERE USER_ID = #{USER_ID} AND TABLE_NAME = #{TABLE_NAME}

	AND SUBJECT_ID = #{SUBJECT_ID}

mapper.delete:
DELETE FROM
TN_USER_STYLE
TN_USER_STYLE_DEFAULT
WHERE USER_ID = #{USER_ID} AND TABLE_NAME = #{TABLE_NAME}
AND SUBJECT_ID = #{SUBJECT_ID}

mapper.update:
UPDATE


		TN_USER_STYLE


		TN_USER_STYLE_DEFAULT


SET
	LAYER_STYLE = #{LAYER_STYLE}
WHERE
	USER_ID = #{USER_ID} AND TABLE_NAME = #{TABLE_NAME}

		AND SUBJECT_ID = #{SUBJECT_ID}
