mapper.insert:

INSERT INTO TN_USER_SUBJECT ( 
	  SUBJECT_ID /* 주제도_id */	
	, USER_ID /* 사용자_아이디 */
	, SUBJECT_NAME /* 주제도_이름 */
	, SUBJECT_GROUP /* 주제도_그룹 */
	, SUBJECT_SHARE /* 주제도_공유범위 */
	, SUBJECT_DESC /* 주제도_설명 */
	, SHARE_STATUS /* 공유승인상태 */
	, BASE /* 공유승인상태 */
	, SYSTEM_MAP /* 공유승인상태 */
) VALUES (
	  #{SUBJECT_ID}  
	, #{USER_ID} 
	, #{SUBJECT_NAME} 
	, #{SUBJECT_GROUP} 
	, #{SUBJECT_SHARE} 
	, #{SUBJECT_DESC} 
	, #{SHARE_STATUS}
	, #{BASE}
	, #{SYSTEM_MAP}
)


INSERT INTO TN_USER_LAYER ( 
	  SUBJECT_ID /* 주제도_id */	
	, LAYER_NAME /* 레이어_이름 */
	, TABLE_NAME /* 테이블_이름 */
	, LAYER_ORDER /* 정렬(요청)_순서 */
	, GROUP_ID /* 레이어_아이디 */
	, GROUP_NAME /* 레이어_아이디 */
	, LAYER_ID /* 레이어_아이디 */
	, LAYER_ONOFF /* 레이어_아이디 */
	, LAYER_TYPE /* 레이어_아이디 */
) VALUES (
	  #{SUBJECT_ID}  
	, #{LAYER_NAME} 
	, #{TABLE_NAME} 
	, #{LAYER_ORDER} 
	, #{GROUP_ID} 
	, #{GROUP_NAME} 
	, #{LAYER_ID, jdbcType=NUMERIC} 
	, #{LAYER_ONOFF, jdbcType=CHAR} 
	, #{LAYER_TYPE, jdbcType=CHAR} 
)

mapper.update:

UPDATE TN_USER_SUBJECT
SET

		SUBJECT_NAME = #{SUBJECT_NAME}


	,SUBJECT_GROUP= #{SUBJECT_GROUP}


	,SUBJECT_DESC= #{SUBJECT_DESC}  


	,SUBJECT_SHARE = #{SUBJECT_SHARE}  


	,SHARE_STATUS = #{SHARE_STATUS}


	,BASE = #{BASE}


	,SYSTEM_MAP = #{SYSTEM_MAP}

WHERE
	SUBJECT_ID=#{SUBJECT_ID} 
	AND  
	USER_ID=#{USER_ID}


UPDATE TN_USER_SUBJECT
SET				

	SUBJECT_GROUP= #{SUBJECT_GROUP}


	,BASE = #{BASE}

WHERE
	SUBJECT_ID=#{SUBJECT_ID} 
	AND  
	USER_ID=#{USER_ID}


UPDATE TN_USER_SUBJECT
SET				

	SHARE_STATUS= #{SHARE_STATUS}

WHERE
	SUBJECT_ID=#{SUBJECT_ID}


UPDATE TN_USER_SUBJECT
SET				

	SYSTEM_MAP= #{SYSTEM_MAP}

WHERE
	SUBJECT_ID=#{SUBJECT_ID}


UPDATE TN_USER_LAYER
SET

		LAYER_NAME = #{LAYER_NAME}


	,TABLE_NAME= #{TABLE_NAME}


	,LAYER_ORDER= #{LAYER_ORDER}  


	,LAYER_ORDER= #{LAYER_ID}  


	,LAYER_ORDER= #{LAYER_ONOFF}  


	,LAYER_ORDER= #{LAYER_TYPE}  

WHERE
	SUBJECT_ID=#{SUBJECT_ID}

mapper.delete:


DELETE FROM TN_USER_LAYER 
WHERE SUBJECT_ID  = #{SUBJECT_ID}
AND  
TABLE_NAME=#{TABLE_NAME}


/* 주제도에 딸린 파일 모두 삭제*/

DELETE FROM TN_SUBJECT_FILE_INFO 
WHERE	
	SUBJECT_ID=#{SUBJECT_ID}
