mapper.insert:
INSERT INTO USV_NOTICE (
	IDX
	,TITLE_TEXT
	,BODY_TEXT
	,WRITE_ID
	,REGIST_DATE
	,EDIT_ID
	,EDIT_DATE
)
VALUES (
	(SELECT NVL(MAX(IDX),0)+1 FROM USV_NOTICE)
	,#{TITLE_TEXT  			, jdbcType=VARCHAR}
	,#{BODY_TEXT 	 		, jdbcType=VARCHAR}
	,#{WRITE_ID 		 		, jdbcType=VARCHAR}
	,TO_CHAR(SYSDATE, 'YYYYMMDD')
	,#{EDIT_ID  				, jdbcType=VARCHAR}
	,TO_CHAR(SYSDATE, 'YYYYMMDD')
)

mapper.update:

UPDATE USV_NOTICE
	SET 
		TITLE_TEXT=#{TITLE_TEXT}
		,BODY_TEXT=#{BODY_TEXT}
		,EDIT_ID=#{EDIT_ID}
		,EDIT_DATE=SYSDATE
	WHERE 
		IDX = #{IDX}

mapper.insert:
 INSERT INTO USV_ATTACHMENT 
 (
 	  FILE_NO
 	  , FILE_NAME
 	  , FILE_PATH
 	  , IDX
 )
 VALUES ( 
	 (SELECT NVL(MAX(FILE_NO),0)+1 FROM USV_ATTACHMENT)
	 ,#{FILE_NAME} 
	 ,#{FILE_PATH}
	 ,
	  	#{IDX}


	  	(SELECT NVL(MAX(IDX),0)+1 FROM USV_NOTICE_BOARD)

 )

mapper.update:
UPDATE USV_ATTACHMENT SET DEL_YN = 'Y' WHERE 1=1
AND FILE_NO IN (${FILE_NO})
AND IDX = #{IDX}

mapper.select:
SELECT * 
	FROM USV_ATTACHMENT 
	WHERE DEL_YN='N'

		AND IDX = #{IDX} 


	    AND	FILE_NO = #{FILE_NO}

mapper.insert:
INSERT INTO USV_REFER_ROOM (
	IDX
	,MANAGE_CODE
	,TITLE_TEXT
	,BODY_TEXT
	,WRITE_ID
	,REGIST_DATE
	,EDIT_ID
	,EDIT_DATE
)
VALUES (
	(SELECT NVL(MAX(IDX),0)+1 FROM USV_REFER_ROOM)
	,#{MANAGE_CODE}
	,#{TITLE_TEXT}
	,#{BODY_TEXT}
	,#{WRITE_ID}
	,TO_CHAR(SYSDATE, 'YYYYMMDD')
	,#{WRITE_ID}
	,TO_CHAR(SYSDATE, 'YYYYMMDD')
)

