mapper.select:
SELECT b.G2_SPATIAL_TYPE
FROM G2S_DATASET a, G2S_GEODATASET b
WHERE a.G2_ID = b.G2_DATASETID AND a.G2_NAME = #{value}

SELECT tnlyrinfo.MAP_ID, g2vlayer.G2_BASEID, tnlyrinfo.G2_ID, tnlyrinfo.LYR_ID, tnlyrinfo.LYR_ENG_NM, tnlyrinfo.LYR_KOREAN_NM as "G2_NAME", tnlyrinfo.LYR_RDL_USE_YN, tnlyrinfo.LYR_WTL_USE_YN, tnlyrinfo.LYR_SWL_USE_YN, tnlyrinfo.LYR_EDIT_YN, tnlyrinfo.LYR_FTR_CDE, tnlyrinfo.LYR_ORDER, tnlyrinfo.LYR_ONOFF, tnlyrinfo.LYR_TYPE FROM TN_LYR_INFO tnlyrinfo INNER JOIN ( SELECT B.G2_BASEID, A.G2_NAME, B.G2_ID AS LAYER_ID, B.G2_NAME AS LAYER_NAME, B.G2_DEFINE FROM (SELECT * FROM G2V_LAYER WHERE G2_BASEID = -1) A INNER JOIN G2V_LAYER B ON (A.G2_ID = B.G2_BASEID) ORDER BY A.G2_ORDER, B.G2_ORDER ) g2vlayer ON (tnlyrinfo.G2_ID = g2vlayer.LAYER_ID) WHERE 1=1
AND LYR_ID = #{LYR_ID}
AND LYR_RDL_USE_YN = #{LYR_RDL_USE_YN}
AND LYR_WTL_USE_YN = #{LYR_WTL_USE_YN}
AND LYR_SWL_USE_YN = #{LYR_SWL_USE_YN}
ORDER BY tnlyrinfo.LYR_ORDER ASC
