mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT PROGRM_FILE_NM , PROGRM_STRE_PATH , PROGRM_KOREAN_NM , dbms_lob.substr(PROGRM_DC, 10, 1)||'...' AS PROGRM_DC , URL FROM USV_PROGRM WHERE 1=1
AND (PROGRM_KOREAN_NM like '%'|| #{searchKeyword}||'%' OR PROGRM_FILE_NM like '%'|| #{searchKeyword}||'%' )
) TB ) WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

mapper.insert:

INSERT INTO USV_PROGRM 
	( PROGRM_FILE_NM
	  , PROGRM_STRE_PATH
	  , PROGRM_KOREAN_NM
	  , PROGRM_DC
	  , URL )
VALUES ( #{progrmFileNm}
	  , #{progrmStrePath}
	  , #{progrmKoreanNm}
	  , #{progrmDc}
	  , #{URL} )

mapper.update:

UPDATE USV_PROGRM 
SET PROGRM_STRE_PATH=#{progrmStrePath}
	, PROGRM_KOREAN_NM=#{progrmKoreanNm}
	, PROGRM_DC      =#{progrmDc}
	, URL            =#{URL}
WHERE PROGRM_FILE_NM=#{progrmFileNm}
