mapper.update:

UPDATE TN_MEMO
SET

		MEMO_INFO = #{MEMO_INFO}


	,MEMO_NM= #{MEMO_NM}


	,X= #{X} /* 사용자_메모.X */


	,Y= #{Y} /* 사용자_메모.X */


	,MAP_SCALE= #{MAP_SCALE} /* 사용자_메모.지도_축척 */


	,MEMO_CN = #{MEMO_CN} /* 사용자_메모.메모_내용 */


	,MARKER_ID = #{MARKER_ID}


	,MARKER_TYPE = #{MARKER_TYPE}

WHERE
	MEMO_ID=#{MEMO_ID}

mapper.select:

/* MemoDAO.selectMemoList */
/* 사용자_메모 조회 */			
SELECT
	  MEMO_ID 				
FROM TN_MEMO
WHERE UPPER(USER_ID) = UPPER(#{USER_ID})
START WITH MEMO_ID=#{MEMO_ID}
CONNECT BY PRIOR MEMO_ID=PARENT_ID

mapper.delete:

/* userMemoDAO.deleteMemoFiles */
/* 메모에 딸린 파일 모두 삭제*/

DELETE FROM TN_MEMO_FILE_INFO 
WHERE	
	MEMO_ID IN					


		#{item}

mapper.select:

/* userMemoDAO.deleteMemoFiles */
/* 메모에 딸린 파일 모두 삭제*/

SELECT FILE_PATH || FILE_UPLOAD_NM FILE_PATH
FROM TN_MEMO_FILE_INFO 
WHERE	
	MEMO_ID IN					


		#{item}


/* userMemoDAO.deleteMemoFiles */
/* 메모에 딸린 파일 모두 삭제*/

SELECT FILE_PATH || FILE_UPLOAD_NM FILE_PATH
FROM TN_MEMO_FILE_INFO 
WHERE	
	FILE_ID	= #{FILE_ID}

mapper.delete:

/* userMemoDAO.deleteMemoFile */
/* 메모파일삭제*/

DELETE FROM TN_MEMO_FILE_INFO 
WHERE	
	FILE_ID = #{FILE_ID}


/* userMemoDAO.deleteMemos */
/* 메모삭제 */

DELETE FROM TN_MEMO 
WHERE MEMO_ID IN		


		#{item}
